import{_ as t,c as o,o as r,ag as l,j as a}from"./chunks/framework.CQuhCYrb.js";const g=JSON.parse('{"title":"基金会发布《2025全球健康报告》","description":"人类健康计划基金会发布了《2025全球健康报告》，报告分析了当前全球健康状况及面临的挑战，并提出了未来健康发展的建议和方向。","frontmatter":{"outline":"deep","date":"2025-12-10T00:00:00.000Z","title":"基金会发布《2025全球健康报告》","description":"人类健康计划基金会发布了《2025全球健康报告》，报告分析了当前全球健康状况及面临的挑战，并提出了未来健康发展的建议和方向。","prev":false,"next":false},"headers":[],"relativePath":"zh-CN/News/20251210-global-health-report.md","filePath":"zh-CN/News/20251210-global-health-report.md","lastUpdated":1766150827000}'),i={name:"zh-CN/News/20251210-global-health-report.md"};function n(s,e,d,h,p,c){return r(),o("div",null,[...e[0]||(e[0]=[l('<h1 id="基金会发布《2025全球健康报告》" tabindex="-1">基金会发布《2025全球健康报告》 <a class="header-anchor" href="#基金会发布《2025全球健康报告》" aria-label="Permalink to &quot;基金会发布《2025全球健康报告》&quot;">​</a></h1><p><strong>发布时间</strong>: 2025-12-10</p><h2 id="报告概述" tabindex="-1">报告概述 <a class="header-anchor" href="#报告概述" aria-label="Permalink to &quot;报告概述&quot;">​</a></h2><p>人类健康计划基金会今日发布了《2025全球健康报告》，这是基金会首次发布全球范围的健康状况报告。报告基于全球100多个国家和地区的健康数据，分析了当前全球健康状况及面临的挑战，并提出了未来健康发展的建议和方向。</p><h2 id="主要发现" tabindex="-1">主要发现 <a class="header-anchor" href="#主要发现" aria-label="Permalink to &quot;主要发现&quot;">​</a></h2><p>报告的主要发现包括：</p><ol><li><strong>全球健康状况总体改善</strong>: 过去十年，全球人均预期寿命从71.4岁提高到73.8岁</li><li><strong>健康不平等问题突出</strong>: 高收入国家与低收入国家的人均预期寿命差距仍达18岁</li><li><strong>非传染性疾病成为主要威胁</strong>: 全球68%的死亡由非传染性疾病引起</li><li><strong>心理健康问题日益严重</strong>: 全球约10亿人受到精神健康问题的困扰</li><li><strong>健康系统韧性面临挑战</strong>: 新冠疫情暴露了全球健康系统的脆弱性</li></ol><h2 id="政策建议" tabindex="-1">政策建议 <a class="header-anchor" href="#政策建议" aria-label="Permalink to &quot;政策建议&quot;">​</a></h2><p>报告提出了以下政策建议：</p><ol><li>加强全球健康治理，建立更公平的健康系统</li><li>加大对初级卫生保健的投入</li><li>推动健康科技创新和数字化转型</li><li>加强健康数据共享和合作</li><li>促进健康公平，缩小健康差距</li></ol><h2 id="社会影响" tabindex="-1">社会影响 <a class="header-anchor" href="#社会影响" aria-label="Permalink to &quot;社会影响&quot;">​</a></h2><p>《2025全球健康报告》的发布将为各国政府、国际组织和健康机构提供重要参考，有助于推动全球健康事业的发展，实现联合国可持续发展目标中的健康相关目标。</p>',12),a("p",null,[a("br"),a("br"),a("a",{href:"index",style:{display:"inline-block",padding:"8px 30px","background-color":"#339900",color:"white !important","text-decoration":"none !important","border-radius":"20px","font-weight":"500"},onmouseover:"this.style.backgroundColor='#000000';",onmouseout:"this.style.backgroundColor='#339900';"},"返回新闻列表")],-1)])])}const _=t(i,[["render",n]]);export{g as __pageData,_ as default};
