import{_ as t,c as o,o as l,ag as r,j as a}from"./chunks/framework.CQuhCYrb.js";const m=JSON.parse('{"title":"首届国际健康系统论坛在北京成功举办","description":"首届国际健康系统论坛在北京成功举办，全球专家共同探讨健康系统创新与发展","frontmatter":{"outline":"deep","date":"2025-12-15T00:00:00.000Z","title":"首届国际健康系统论坛在北京成功举办","description":"首届国际健康系统论坛在北京成功举办，全球专家共同探讨健康系统创新与发展","prev":false,"next":false},"headers":[],"relativePath":"zh-CN/News/20251215-international-health-forum.md","filePath":"zh-CN/News/20251215-international-health-forum.md","lastUpdated":1766150827000}'),i={name:"zh-CN/News/20251215-international-health-forum.md"};function n(h,e,s,d,u,p){return l(),o("div",null,[...e[0]||(e[0]=[r('<h1 id="首届国际健康系统论坛在北京成功举办" tabindex="-1">首届国际健康系统论坛在北京成功举办 <a class="header-anchor" href="#首届国际健康系统论坛在北京成功举办" aria-label="Permalink to &quot;首届国际健康系统论坛在北京成功举办&quot;">​</a></h1><p><strong>发布时间</strong>: 2025-12-15</p><h2 id="论坛概况" tabindex="-1">论坛概况 <a class="header-anchor" href="#论坛概况" aria-label="Permalink to &quot;论坛概况&quot;">​</a></h2><p>由人类健康计划基金会主办的首届国际健康系统论坛在北京成功举办，来自全球20多个国家的300余名专家学者参加了本次论坛，共同探讨健康系统创新与发展。</p><h2 id="论坛主题" tabindex="-1">论坛主题 <a class="header-anchor" href="#论坛主题" aria-label="Permalink to &quot;论坛主题&quot;">​</a></h2><p>本次论坛以&quot;健康系统创新与可持续发展&quot;为主题，围绕健康系统理论、健康数据共享、人工智能在健康领域的应用、全球健康治理等议题展开深入讨论。</p><h2 id="重要嘉宾" tabindex="-1">重要嘉宾 <a class="header-anchor" href="#重要嘉宾" aria-label="Permalink to &quot;重要嘉宾&quot;">​</a></h2><p>出席本次论坛的重要嘉宾包括：</p><ul><li>世界卫生组织总干事</li><li>中国国家卫生健康委员会主任</li><li>美国国立卫生研究院院长</li><li>英国皇家医学会会长</li><li>全球健康领域知名专家学者</li></ul><h2 id="论坛成果" tabindex="-1">论坛成果 <a class="header-anchor" href="#论坛成果" aria-label="Permalink to &quot;论坛成果&quot;">​</a></h2><p>本次论坛取得了丰硕成果：</p><ol><li>发布了《国际健康系统创新宣言》</li><li>成立了&quot;全球健康系统创新联盟&quot;</li><li>启动了10个国际健康合作项目</li><li>签署了5项国际健康合作协议</li></ol><h2 id="媒体报道" tabindex="-1">媒体报道 <a class="header-anchor" href="#媒体报道" aria-label="Permalink to &quot;媒体报道&quot;">​</a></h2><p>本次论坛受到了全球媒体的广泛关注，包括新华社、人民日报、BBC、CNN等国内外主流媒体均对论坛进行了报道。</p><h2 id="后续活动" tabindex="-1">后续活动 <a class="header-anchor" href="#后续活动" aria-label="Permalink to &quot;后续活动&quot;">​</a></h2><p>人类健康计划基金会计划每年举办一次国际健康系统论坛，将其打造成为全球健康领域的重要交流平台。</p>',16),a("p",null,[a("br"),a("br"),a("a",{href:"index",style:{display:"inline-block",padding:"8px 30px","background-color":"#339900",color:"white !important","text-decoration":"none !important","border-radius":"20px","font-weight":"500"},onmouseover:"this.style.backgroundColor='#000000';",onmouseout:"this.style.backgroundColor='#339900';"},"返回新闻列表")],-1)])])}const f=t(i,[["render",n]]);export{m as __pageData,f as default};
